mapper.select:
select ttan.id,tu.name as owner,ttan.money,ttan.name,ttan.expected_date,ttan.customer_id, tdv.value as stage,tdv1.value as type,tdv2.value as source,ttan.activity_id,ttan.contacts_id, tu1.name as create_by,ttan.create_time,tu2.name as edit_by,ttan.edit_time,ttan.description, ttan.contact_summary,ttan.next_contact_time from tbl_transaction ttan join tbl_user tu on ttan.owner = tu.id join tbl_user tu1 on ttan.create_by = tu1.id join tbl_dictionary_value tdv on ttan.stage = tdv.id left join tbl_dictionary_value tdv1 on ttan.type = tdv1.id left join tbl_dictionary_value tdv2 on ttan.source = tdv2.id left join tbl_user tu2 on ttan.edit_by = tu2.id where ttan.customer_id in
#{customerId,jdbcType=CHAR}
order by ttan.create_time desc

mapper.delete:

delete from tbl_transaction
where contacts_id in

    #{contactsId,jdbcType=CHAR}

mapper.select:
select ttan.id,tu.name as owner,ttan.money,ttan.name,ttan.expected_date,ttan.customer_id, tdv.value as stage,tdv1.value as type,tdv2.value as source,ttan.activity_id,ttan.contacts_id, tu1.name as create_by,ttan.create_time,tu2.name as edit_by,ttan.edit_time,ttan.description, ttan.contact_summary,ttan.next_contact_time from tbl_transaction ttan join tbl_user tu on ttan.owner = tu.id join tbl_user tu1 on ttan.create_by = tu1.id join tbl_dictionary_value tdv on ttan.stage = tdv.id left join tbl_dictionary_value tdv1 on ttan.type = tdv1.id left join tbl_dictionary_value tdv2 on ttan.source = tdv2.id left join tbl_user tu2 on ttan.edit_by = tu2.id where ttan.contacts_id in
#{contactsId,jdbcType=CHAR}
order by ttan.create_time desc

select ttan.id,tu.name as owner,ttan.money,ttan.name,ttan.expected_date,ttan.customer_id, tdv.value as stage,tdv1.value as type,tdv2.value as source,ttan.activity_id,ttan.contacts_id, tu1.name as create_by,ttan.create_time,tu2.name as edit_by,ttan.edit_time,ttan.description, ttan.contact_summary,ttan.next_contact_time,tcu.name as customer_name, concat(tco.full_name,tco.appellation) as contacts_name from tbl_transaction ttan join tbl_user tu on ttan.owner = tu.id join tbl_user tu1 on ttan.create_by = tu1.id join tbl_dictionary_value tdv on ttan.stage = tdv.id left join tbl_dictionary_value tdv1 on ttan.type = tdv1.id left join tbl_dictionary_value tdv2 on ttan.source = tdv2.id left join tbl_user tu2 on ttan.edit_by = tu2.id left join tbl_customer tcu on ttan.customer_id = tcu.id left join (select tco.id,tco.full_name,tdv.value as appellation from tbl_contacts tco left join tbl_dictionary_value tdv on tco.appellation = tdv.id) tco on ttan.contacts_id = tco.id
and tu.name like '%' #{owner} '%'
and ttan.name like '%' #{name} '%'
and tcu.name like '%' #{customerName} '%'
and tdv.id = #{stage}
and tdv1.id = #{type}
and tdv2.id = #{source}
and (tco.full_name like '%' #{contactsName} '%' or tco.appellation like '%' #{contactsName} '%')
order by ttan.create_time desc limit #{beginNo},#{pageSize}

select count(*) from tbl_transaction ttan join tbl_user tu on ttan.owner = tu.id join tbl_user tu1 on ttan.create_by = tu1.id join tbl_dictionary_value tdv on ttan.stage = tdv.id left join tbl_dictionary_value tdv1 on ttan.type = tdv1.id left join tbl_dictionary_value tdv2 on ttan.source = tdv2.id left join tbl_user tu2 on ttan.edit_by = tu2.id left join tbl_customer tcu on ttan.customer_id = tcu.id left join (select tco.id,tco.full_name,tdv.value as appellation from tbl_contacts tco left join tbl_dictionary_value tdv on tco.appellation = tdv.id) tco on ttan.contacts_id = tco.id
and tu.name like '%' #{owner} '%'
and ttan.name like '%' #{name} '%'
and tcu.name like '%' #{customerName} '%'
and tdv.value = #{stage}
and tdv1.value = #{type}
and tdv2.value = #{source}
and (tco.full_name like '%' #{contactsName} '%' or tco.appellation like '%' #{contactsName} '%')

